import React, { useEffect, useState } from 'react';
import classes from './UploadBox.module.css'

const UploadBox = ({display, fileName, sizeError}) => {  
    const [test, setTest] = useState(false);
    
    useEffect( () => {
        [classes.lineLoad]=classes.lineLoad
    }, [fileName]
    )

    if (!display) return null;

    // console.log(test)
    // setTimeout (
    //     () => {setTest(true)}, 3000
    // );
    // console.log(test)

    return (
        <div className={classes.main}>
            <div className={classes.fileImg}/>
            {sizeError
                ?<div><p className={classes.SizeError}>Your file is too big!</p><div className={classes.lineError}/></div>
                :<div><p disabled={test} className={classes.fileName}>{fileName}</p><div className={classes.line}/><div className={classes.lineLoad}/></div>
            }
            <button/>           
        </div>
    );
};

export default UploadBox;
