import React, { useEffect, useState } from "react";
import ModalWindow from './UI/modal/ModalWindow';
import MyInput from './UI/input/MyInpit.jsx';
import ExitButton from './UI/button/ExitButton';
import MyButton from './UI/button/MyButton';


const ReviewWindow = ({ modalWindow, setModal }) => {
    const [count, setCount] = useState(0);
    const [names, setNames] = useState('');
    const [validButton, setValidButton] = useState(false);


    useEffect(() => {
        if (!names || !names.split(' ').join('').match(/^[a-zA-Zа-яА-Я]+$/)) {
            setValidButton(true)
        } else {
            setValidButton(false)
        }
    }, [names]
    )

    const namesHandler = (e) => {
        setNames(e.target.value)
    }

    const handleFileUpload = event => {
        console.log(event.target.files[0].name);
      };

    return (
        <ModalWindow visible={modalWindow} setVisible={setModal}>
            <h3 className="reviewsHead">Отзыв</h3>
            <ExitButton click={setModal} style={{ position: "absolute", left: "631.46px", top: "26px" }} />
            <h4 className="reviewsLine1">Как вас зовут?</h4>
            <MyInput onChange={e => namesHandler(e)} value={names} style={{ position: "absolute", left: "24px", width: "395px", height: "52px", top: "106px" }} type="text" placeholder="Имя Фамилия" />
            <input
                ref="fileInput"
                onChange={handleFileUpload}
                type="file"
                style={{ display: "none" }}
            // multiple={false}
            />
            <MyButton onClick={() => refs.fileInput.click()} disabled={validButton} visible={true} style={{ position: "absolute", width: "217px", left: "435px", top: "106px" }}>Загрузить фото</MyButton>
            <h4 className="reviewsLine2">Все ли вам понравилось?</h4>
            <textarea className="textarea" maxLength="200" onChange={e => setCount(e.target.value.length)} placeholder="Напишите пару слов о вашем опыте..." ></textarea>
            <MyButton visible={false} style={{ position: "absolute", width: "203px", left: "24px", top: "367px" }}>Отправить отзыв</MyButton>
            <div className="iconInfo" />
            <p className="warn">Все отзывы проходят модерацию в течение 2 часов</p>
            <p className="count">{count}/200</p>
        </ModalWindow>
    );
};

export default ReviewWindow;
