import React, { useState } from "react";
import ModalWindow from './UI/modal/ModalWindow';
import MyInput from './UI/input/MyInpit.jsx';
import ExitButton from './UI/button/ExitButton';
import MyButton from './UI/button/MyButton';


const ReviewWindow = (modalVision) => {
    const [modalWindow, setModalWindow] = useState(false);
    const [count, setCount] = useState(0);

    return (
        <ModalWindow visible={modalWindow} setVisible={setModalWindow}>
        <h3 className="reviewsHead">Отзыв</h3>
        <ExitButton click={setModalWindow} style={{ position: "absolute", left: "631.46px", top: "26px" }} />
        <h4 className="reviewsLine1">Как вас зовут?</h4>
        <MyInput style={{ position: "absolute", left: "24px", width: "395px", height: "52px", top: "106px" }} type="text" placeholder="Имя Фамилия" />
        <MyButton  visible={true} style={{ position: "absolute", width: "217px", left: "435px", top: "106px" }}>Загрузить фото</MyButton>
        <h4 className="reviewsLine2">Все ли вам понравилось?</h4>
        <textarea className="textarea" maxLength="200" onChange={e => setCount(e.target.value.length)} placeholder="Напишите пару слов о вашем опыте..." ></textarea>
        <MyButton visible={false} style={{ position: "absolute", width: "203px", left: "24px", top: "367px" }}>Отправить отзыв</MyButton>
        <div className="iconInfo" />
        <p className="warn">Все отзывы проходят модерацию в течение 2 часов</p>
        <p className="count">{count}/200</p>
      </ModalWindow>
    );
};

export default ReviewWindow;
