import React, { useEffect, useState } from 'react';
import Loader from './loader/Loader'
import classes from './UploadBox.module.css'

const UploadBox = ({display, fileName, sizeError}) => {  
   const [loading, setLoading] = useState(true);
    
    useEffect( () => {
        setTimeout(
            setLoading(false),2000
        )
    }, [fileName]
    )

    if (!display) return null;

    // console.log(test)
    // setTimeout (
    //     () => {setTest(true)}, 3000
    // );
    // console.log(test)

    return (
        <div className={classes.main}>
            <div className={classes.fileImg}/>
            {sizeError
                ?<div><p className={classes.SizeError}>Your file is too big!</p><div className={classes.lineError}/></div>
                :loading ? <div><p className={classes.fileName}>{fileName}</p><div className={classes.line}/><Loader/></div>
                :<div><p className={classes.fileName}>{fileName}</p><div className={classes.lineLoaded}/></div>
            }
            
        </div>
    );
};

export default UploadBox;
