import React from "react";
import classes from '../styles/Toast.module.css'

const Toast = ({toast, setToast}) => {
    const rootClasses = [classes.Toast];

    if(toast) {
        rootClasses.pop()
        rootClasses.push(classes.active)
        setToast(false)
    }

    return(
        <div className={rootClasses} />
    );
};

export default Toast;
