import React, { useState } from 'react';
import classes from './UploadBox.module.css'

const UploadBox = ({display, fileName, sizeError}) => {  
    const [test, setTest] = useState(false);
    if (!display) return null;

    console.log(test)
    setTimeout (
        () => {setTest(true)}, 3000
    );
    console.log(test)

    return (
        <div className={classes.main}>
            <div className={classes.fileImg}/>
            <p disabled={test} className={classes.fileName}>
            {sizeError
                ?<p className={classes.SizeError}>Your file is too big!</p>
                :{fileName}
            }
            </p>
        </div>
    );
};

export default UploadBox;
